package LeetCodeTasks;
import java.util.ArrayList;
import java.util.Arrays;
import java.util.HashMap;
import java.util.List;

/**
 * @TaskName Find All Anagrams in a String
 * @TaskDetails Given two strings s and p, return an array of all the start
 *              indices of p's anagrams in s. You may return the answer in any
 *              order. An Anagram is a word or phrase formed by rearranging the
 *              letters of a different word or phrase, typically using all the
 *              original letters exactly once.
 * @Source https://leetcode.com/problems/find-all-anagrams-in-a-string/
 */

public class Task11_FindAllAnnagrams {
	public static void run() {
		String[][] test = {
				{ "", "", "Both string is empty" },
				{ "", "abc", "Search string is empty" },
				{ "abc", "", "Target string is empty" },
				{ "abc", "abcd", "Target longer then serach string" },
				{ "cbaebabacd", "abc", "First and last elements are valid" },
				{ "",
					"",
					"LeetCode long task" },
				{ "abab", "ab", "Three elements are valid" } 
		};

		for (String[] a : test) {
			System.out.println("Start test: " + a[2]);
			System.out.println("Search string: " + a[0] + "\n    Target is: " + a[1]);
			System.out.println("    Answer is: " + Arrays.toString(findAnagramsLeetCode(a[0], a[1]).toArray())
					+ "\n=============================");
		}

	}

	public static List<Integer> findAnagrams(String s, String p) {
		ArrayList<Integer> answer = new ArrayList<Integer>();
		if (s == null || p == null || s.isEmpty() || p.isEmpty() || p.length() > s.length()) {
			return answer;
		}
		char[] searchArray;
		char[] target = p.toCharArray();
		Arrays.sort(target);
		String targetString = new String(target);
		int len = p.length();
		int finish = s.length() - len + 1;
		for (int i = 0; i < finish; i++) {
			searchArray = s.substring(i, (i + len)).toCharArray();
			Arrays.sort(searchArray);
			String searchString = new String(searchArray);
			if (targetString.equals(searchString)) {
				answer.add(i);
			}
		}
		return answer;
	}

	public static List<Integer> findAnagramsWithHashMap(String s, String p) {
		ArrayList<Integer> answer = new ArrayList<Integer>();
		if (s == null || p == null || s.isEmpty() || p.isEmpty() || p.length() > s.length()) {
			return answer;
		}
		// Create HashMap
		HashMap<Character, Integer> target = new HashMap<>();
		HashMap<Character, Integer> targetCopy;
		for (char c : p.toCharArray()) {
			if (target.containsKey(c)) {
				target.put(c, (target.get(c) + 1));
			} else {
				target.put(c, 1);
			}
		}

		int len = s.length() - p.length() + 1;
		int index = 0;
		int count = p.length();
		while (index < len) {
			targetCopy = new HashMap<>(target);
			count = p.length();
			for (int j = 0; j < p.length(); j++) {
				char c = s.charAt(index + j);
				if (count > 0) {
					if (targetCopy.containsKey(c) && targetCopy.get(c) > 0) {
						targetCopy.put(c, (targetCopy.get(c) - 1));
						count--;
					} else {
						index = index +j+ 1;
						break;
					}
				}
				if (count == 0) {
					answer.add(index);
					index = index  + 1;
					break;
				}
			}
		}
		return answer;
	}
	
	public static  List<Integer> findAnagramsLeetCode(String s, String p) {
		 List<Integer> list = new ArrayList<>();
		    if (s == null || s.length() == 0 || p == null || p.length() == 0) return list;
		    int[] hash = new int[256]; //character hash
		    //record each character in p to hash
		    for (char c : p.toCharArray()) {
		        hash[c]++;
		    }
		    //two points, initialize count to p's length
		    int left = 0, right = 0, count = p.length();
		    while (right < s.length()) {
		        //move right everytime, if the character exists in p's hash, decrease the count
		        //current hash value >= 1 means the character is existing in p
		        if (hash[s.charAt(right++)]-- >= 1) count--; 
		        
		        //when the count is down to 0, means we found the right anagram
		        //then add window's left to result list
		        if (count == 0) list.add(left);
		    
		        //if we find the window's size equals to p, then we have to move left (narrow the window) to find the new match window
		        //++ to reset the hash because we kicked out the left
		        //only increase the count if the character is in p
		        //the count >= 0 indicate it was original in the hash, cuz it won't go below 0
		        if (right - left == p.length() && hash[s.charAt(left++)]++ >= 0) count++;
		    }
		    return list;
    }
}
